Method.Implementation:
set borderMax = 10000 set borderMin = 0 set unitName = "-"
if (parentRegionGuid="") set parentRegionGuid = "NULL"
set border = ""
try{ if (..BordersIdxExists(parentRegionGuid, paramId, hLevel) = 1){ set border = ..BordersIdxOpen(parentRegionGuid, paramId, hLevel) } else{ set minVal=0, maxVal=10000
if (parentRegionGuid = "NULL"){ &sql(SELECT Min(Value), Max(Value) INTO :minVal,:maxVal FROM USA.Region as reg INNER JOIN USA.ParameterValue as pv ON reg.ID = pv.Area WHERE reg.HLevel=:hLevel AND pv.Parameter=:paramId ) } else{ &sql(SELECT Min(Value), Max(Value) INTO :minVal,:maxVal FROM USA.Region as reg INNER JOIN USA.ParameterValue as pv ON reg.ID = pv.Area WHERE reg.HLevel=:hLevel AND pv.Parameter=:paramId AND reg.ParentRegion=:parentRegionGuid) }
set border = ..%New() set border.ParentRegion = parentRegionGuid set border.Parameter = ##class(USA.Parameter).%OpenId(paramId) set border.HLevel = hLevel set border.Maximum = maxVal set border.Minimum = minVal do border.%Save() }
set borderMin = border.Minimum set borderMax = border.Maximum set unitName = border.Parameter.UnitName } catch {}
quit borderMin_";"_borderMax_";"_unitName

set st = $$$OK
try{
	if (parentRegionGuid="") set parentRegionGuid = "NULL"
	set brd = ..BordersIdxOpen(parentRegionGuid, paramId, level)
	set brd.Maximum = max
	set brd.Minimum = min
	set st = brd.%Save()
}
catch(ex){
	set st = ex.AsStatus()
}
quit st
